import {useState} from 'react'
import { hero } from "../../dummyData";
import Card from './Card';
function Hero() {
    const [items, setitems] = useState(hero)
    return (
        <>
        <section className='hero'>
            <div className="container">
                {(items).map((item)=>{
return <Card key={item.id} item={item}/>
                })}
            </div>
            
        </section>
        </>
    )
}

export default Hero
