import './post.css'

import TopHeading from "../../components/topHeading/TopHeading";
import { ppost } from "../../dummyData";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCalendarDays, faComment } from "@fortawesome/free-solid-svg-icons";
import Slider from "react-slick";

export default function Post() {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 2,
    slidesToScroll: 1,
  };

  return (
    <>
      <div className="popularPost">
        <TopHeading title="Popular Post" />
        <div className="content">
          <Slider {...settings}>
            {ppost.map((val) => {
              return (
                <div className="items">
                  <div className="box shadow">
                    <div className="images row">
                      <div className="img">
                        <img src={val.cover} alt="" />
                      </div>
                      <div className="catgeoryPopular">
                        <span>{val.catgeory}</span>
                      </div>
                    </div>

                    <div className="textRow">
                      <h1 className="titlePopular">
                        {val.title.slice(0, 40)}...
                      </h1>
                      <div className="datePopular">
                        <FontAwesomeIcon
                          className="iLast"
                          icon={faCalendarDays}
                        />
                        <label>{val.date}</label>
                      </div>
                      <div className="commentPopular">
                        <FontAwesomeIcon className="iLast" icon={faComment} />
                        <label>{val.comments}</label>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </Slider>
        </div>
      </div>
    </>
  );
};
